import React, { useState, useEffect } from "react";
import { StyleSheet, Text, SafeAreaView, View, TouchableOpacity, ScrollView, Image } from "react-native";
import { StatusBar } from "expo-status-bar"
import fileImage from '../../image/file-solid.png'
import folderImage from '../../image/folder-solid.png'
import api from '../../API'

export default function Manager() {
    const [path, setPath] = useState('')
    const [dir, setDir] = useState('')

    useEffect(() => {
        api.get('/main').then(({ data }) => {
            setPath(data.path)

            api.post('/', { path: data.path }).then(({ data }) => {
                setDir(data)
            })
        })
    }, [])

    function pathRaplace() {
        let array = path.split('/')
        array.shift()
        return array
    }

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <StatusBar style="light" />
            <View style={styles.header}>
                <Text style={styles.textHeader}>Folder Manager</Text>
                <View style={styles.viewPath}>
                    {
                        pathRaplace()?.map((path, index) => (
                            <Text
                                key={index}
                                style={
                                    (index === pathRaplace().length - 1) ? styles.textPathLast : styles.textPath}>
                                {path} {(index === pathRaplace().length - 1) ? "" : '> '}
                            </Text>
                        ))
                    }
                </View>
            </View >
            <ScrollView style={{ flex: 1 }} scrollEventThrottle={100} >
                <View style={styles.container}>
                    {
                        dir.folder?.map((f, index) => (
                            <TouchableOpacity style={styles.btnData} key={index}>
                                <Image key={index} source={folderImage}></Image>
                                <Text key={index}> {f}</Text>
                            </TouchableOpacity>
                        ))

                    }
                    {
                        dir.file?.map((f, index) => (
                            <TouchableOpacity style={styles.btnData} key={index}>
                                <Image style ={styles.image} key={index} source={fileImage}></Image>
                                <Text key={index}>{f}</Text>
                            </TouchableOpacity>
                        ))
                    }

                </View>
            </ScrollView>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    header: {
        padding: 30,
        paddingBottom: 10,
        backgroundColor: "#00BFFF",
        borderBottomColor: '#808080',
        borderWidth: 1,
    },
    textHeader: {
        color: '#DCDCDC',
        textAlign: 'center',
        fontSize: 25,
        marginTop: 20,
        fontWeight: 'bold',
    },
    viewPath: {
        display: 'flex',
        flexDirection: 'row',
    },
    textPath: {
        color: '#ffff',
        marginRight: 3
    },
    textPathLast: {
        color: '#41788C',
        borderBottomColor: '#41788C',
        borderBottomWidth: 1,
        paddingLeft: 3,
        paddingBottom: 2
    },
    container: {
        backgroundColor: '#808080'
    },
    btnData: {
        borderTopColor: '#808080',
        borderBottomColor: '#808080',
        borderBottomWidth: StyleSheet.hairlineWidth,
        borderTopWidth: StyleSheet.hairlineWidth,
        marginBottom: 1,
        backgroundColor: '#ffffff',
        padding: 30
    },
    image: {
        width:15,
        height:20,
    }
})
